import React, { UseState,Component } from "react";
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import SocialFollow from "./Socialfollow";
import { Container, Row, Col, Button } from "react-bootstrap";
import SignUp from "./MutlistepForm/images/signup.png";
import CheckButton from "react-validation/build/button";
import AuthService from "../services/auth.service";
import "./style.css"
import {Link} from "react-router-dom";


const required = value => {
  if (!value) {
    return (
      <div className="alert alert-danger" role="alert">
        This field is required!
      </div> 
    );
  }
};
const vuser = value => {
  if (value.length < 3 || value.length > 20) {
    return (
      <div className="alert alert-danger" role="alert">
        The username must be between 3 and 20 characters.
      </div>
    );
  }
};
const vemail = value => {
  if (value.length < 3 || value.length > 20) {
    return (
      <div className="alert alert-danger" role="alert">
        The username must be between 3 and 20 characters.
      </div>
    );
  }
};
const vpassword = value => {
  if (value.length < 6 || value.length > 40) {
    return (
      <div className="alert alert-danger" role="alert">
        The password must be between 6 and 40 characters.
      </div>
    );
  }
};

const vnumber  = value => {
  if (value.length === 11) {
    return (
      <div className="alert alert-danger" role="alert">
        The Mobile_number must be 10 digit.
      </div>
    );
  }
};
export default class Register extends Component {
  constructor(props) {
     super(props);
    this.handleRegister = this.handleRegister.bind(this);
    this.onChangeUserName = this.onChangeUser.bind(this);
    this.onChangeEmail = this.onChangeEmail.bind(this);
    this.onChangNumber = this.onChangNumber.bind(this);
    this.onChangePassword = this.onChangePassword.bind(this);
    this.state = {
      Username:"",
      Email: "",
      Mobile_no:"",
      Password: "",
      successful: false,
      message: ""
    };
  }


  /* onValueChange(event) {
    this.setState({
      selectedOption: event.target.value
    });
  }
 */
  onChangeUser(e) {
    this.setState({
      Username: e.target.value
    });
  }
  onChangeEmail(e) {
    this.setState({
      Email: e.target.value
    });
  }
  onChangNumber(e) {
    this.setState({
      Mobile_no: e.target.value
    });
  }

  onChangePassword(e) {
    this.setState({
      Password: e.target.value
    });
  }


  handleRegister(e) {
    e.preventDefault();
    this.setState({
      message: "",
      successful: false
    });
    this.form.validateAll();
    if (this.checkBtn.context._errors.length === 0) {
      AuthService.register(
        this.state.Username,
        this.state.Email,
        this.state.Mobile_no,
        this.state.role,
        this.state.Password
      ).then(
        response => {          this.setState({
            message: response.data.message,
            successful: true
          });
        },
        error => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();
          this.setState({
            successful: false,
            message: resMessage
          });
        }
      );
    }
  }
  render() {
    return (
      <Container className="Main d-flex justify-content-center p-5 shadow-lg  align-item-center">

      <Row className="">

      <Col lg={6} md={6} sm={12} className="Imgclass" style={{backgroundColor:"#4481eb"}}>

        <img className="w-100 p-3" src={SignUp} alt="icon"/>
        <h6 className="text-center mt-2">Welcome to Aksesso<br/>
        Bright Future is waiting for you.  
        </h6>
        </Col>

        <Col lg={6} md={6} sm={12} className="text-center pt-4">
        <h1 className=" heading text-center fw-bolder" style={{fontColor:"#4481eb"}}>SIGN UP</h1>



          <Form onSubmit={this.handleRegister} ref={c =>{
            this.form = c;
          }}>
          {!this.state.successful && (
            <div>
              <div className="form-group">
                <Input
                  type="text"
                  className="form-control"
                  name="Username"
                  value={this.state.Username}
                  placeholder="Username"
                  validations={[required, vuser]}
                />
              </div>
              <div className="form-group">
                <Input
                  type="email"
                  className="form-control"
                  name="Email"
                  value={this.state.Email}
                  placeholder="Email"
                  validations={[required, vemail]}
                />
              </div>
              <div className="form-group">
                <Input
                  type="text"
                  className="form-control"
                  name="Mobile_no"
                  value={this.state.Mobile_no}
                  placeholder="Mobile_no"
                  onChange={this.onChangNumber}
                  validations={[required, vnumber]}
                />
              </div>
              <div className="form-group">
                <Input
                  type="password"
                  className="form-control"
                  name="Password"
                  placeholder="Password"
                  value={this.state.Password}
                  onChange={this.onChangePassword}
                  validations={[required, vpassword]}
                />
              </div>

              <div className="form-group">
                <button className="btn  my-3 btn-primary btn-block">Sign Up</button>
              </div>
            </div>
          )}
          {this.state.message && (
            <div className="form-group">
              <div className={ this.state.successful
                ? "alert alert-success"
                : "alert alert-danger" 
              }
               role="alert">  
                {this.state.message}
              </div>
            </div>
          )}
          <CheckButton 
          style={{ display: "none" }}
          ref={c => {
            this.checkBtn = c;
          }} />
          <div className="text-center mt-1 mb-1">
          <Link to={"/login"} className="nav-link">
          <small className="reset mr-3">Already User ?</small>
          </Link>
            </div>

            <div className="SocialMedia">
            <SocialFollow/>
            </div>

        </Form>
      </Col>

      </Row>
    </Container>
    );
  }
}
